import React, { useState } from 'react';
import AWS from 'aws-sdk';
import ConfirmationModal from './ConfirmationModal';
import './Checkout.css';

const Checkout = ({ clearCart }) => {
  const [userData, setUserData] = useState({
    name: '',
    family_name: '', 
    email: '',
    phone_number: '', 
    address: '', 
    locale: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData({ ...userData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Do something with userData (e.g., submit order)
  
    // Update user attributes in Cognito
    try {
      const cognitoIdentity = new AWS.CognitoIdentityServiceProvider();
      const params = {
        UserAttributes: [
          { Name: 'name', Value: userData.name },
          { Name: 'family_name', Value: userData.family_name },
          { Name: 'email', Value: userData.email },
          { Name: 'phone_number', Value: userData.phone_number },
          { Name: 'address', Value: userData.address },
          { Name: 'locale', Value: userData.locale }
          // Add any other attributes you want to update
        ],
        UserPoolId: 'us-east-1_X4cZfd6Ch', // Replace with your user pool ID
        Username: 'USERNAME' // Replace with the username of the current user
      };
  
      await cognitoIdentity.adminUpdateUserAttributes(params).promise();
      console.log('User attributes updated successfully');
    } catch (error) {
      console.error('Error updating user attributes:', error);
      // Handle error (e.g., display an error message to the user)
    }
  
    clearCart();
    setShowConfirmationModal(true);
  };
  

  const closeConfirmationModal = () => {
    setShowConfirmationModal(false);
    // Navigate to home or any other relevant page after confirmation
  };

  const [showConfirmationModal, setShowConfirmationModal] = useState(false);

  return (
    <div className="CheckoutContainer">
      <div className="Checkout">
        <h2>Checkout</h2>
        <form onSubmit={handleSubmit}>
          <label htmlFor="name">First Name</label>
          <input 
            type="text" 
            name="name" 
            id="name"
            value={userData.name} 
            onChange={handleChange}
          />

          <label htmlFor="family_name">Last Name</label>
          <input 
            type="text" 
            name="family_name" 
            id="family_name"
            value={userData.family_name} 
            onChange={handleChange} 
            required 
          />

          <label htmlFor="email">Email</label>
          <input 
            type="email" 
            name="email" 
            id="email"
            value={userData.email} 
            onChange={handleChange} 
            required 
          />

          <label htmlFor="phone_number">Phone Number</label>
          <input 
            type="text" 
            name="phone_number" 
            id="phone_number"
            value={userData.phone_number} 
            onChange={handleChange} 
            required 
          />

          <label htmlFor="address">Address</label>
          <input 
            type="text" 
            name="address" 
            id="address"
            value={userData.address} 
            onChange={handleChange} 
            required 
          />

          <label htmlFor="locale">Country</label>
          <input 
            type="text" 
            name="locale" 
            id="locale"
            value={userData.locale} 
            onChange={handleChange} 
            required 
          />

          <button type="submit" className="ContinueButton">Submit Order</button>
        </form>
      </div>
      {showConfirmationModal && (
        <ConfirmationModal closeModal={closeConfirmationModal} />
      )}
    </div>
  );
};

export default Checkout;
